<?php	class SessionStorage {				public static function add($key, $value) {			$_SESSION[$key] = $value;		}				public static function get($key) {			if (isset($_SESSION[$key])) return $_SESSION[$key];			else {				throw (new Exception('session key does not exists'));				return false;			}		}				public static function edit($key, $value) {			if (isset($_SESSION[$key])) {				$_SESSION[$key] = $value;				return true;			}			else {				throw (new Exception('session key does not exists'));				return false;			}		}				public static function remove($key) {			if (isset($_SESSION[$key])) {				unset($_SESSION[$key]);				return true;			} else {				return false;			}		}				public static function getStorageObject() {			return $_SESSION;		}			}?>