INSERT INTO `vl1_MenuModel` (`type`, `menuItemTitle`, `id`, `link`, `modelId`, `treeItemId`, `content`, `r_id`, `lang_id`, `visible`, `pageId`, `clickable`, `newColumn`, `class`) VALUES
(1, '"Üzümçülük və şərabçılıq haqqında"  Azərbaycan Respublikasının Qanunu', 289, NULL, 0, 97, '<p>&nbsp;</p>\r\n<p style="text-align: center;"><span><strong><img src="http://sorttoxumagro.gov.az/public/gerb.png" alt="" width="105" height="113" /></strong></span></p>\r\n<p style="text-align: center;"><span><strong>&Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq haqqında</strong></span></p>\r\n<p style="text-align: center;"><strong>Azərbaycan Respublikasının Qanunu</strong></p>\r\n<p style="text-align: center;"><span><strong>&nbsp;</strong></span></p>\r\n<p>Bu Qanun Azərbaycan Respublikasında &uuml;z&uuml;m&ccedil;&uuml;l&uuml;y&uuml;n təşkilinin, &uuml;z&uuml;m və şərab&ccedil;ılıq məhsulları istehsalının və d&ouml;vriyyəsinin tənzimlənməsinin, habelə keyfiyyətinin təmin edilməsinin h&uuml;quqi əsaslarını m&uuml;əyyən edir.</p>\r\n<p><span><strong>I fəsil</strong></span></p>\r\n<p><span><strong>&Uuml;MUMİ M&Uuml;DDƏALAR</strong></span></p>\r\n<p><span>Maddə 1.&nbsp;Əsas anlayışlar</span></p>\r\n<p><span>1.0. Bu Qanunda istifadə edilən anlayışlar aşağıdakı mənaları ifadə edir:</span></p>\r\n<p><span>1.0.1.&nbsp;&uuml;z&uuml;m&ccedil;&uuml;l&uuml;k&nbsp;- &uuml;z&uuml;m&uuml;n istehsalı ilə məşğul olan kənd təsərr&uuml;fatı sahəsi;</span></p>\r\n<p><span>1.0.2.&nbsp;&uuml;z&uuml;m əkini materialları&nbsp;- &uuml;z&uuml;ml&uuml;klərin salınmasında istifadə edilən, habelə &ouml;z k&ouml;k&uuml; &uuml;st&uuml;ndə və ya calaq edilib yetişdirilən tinglər, calaqaltı və calaq&uuml;st&uuml; &ccedil;ubuqlar;</span></p>\r\n<p><span>1.0.3.&nbsp;&uuml;z&uuml;m məhsulları&nbsp;- təbii və ya emal olunmuş halda istehlak edilən s&uuml;frə &uuml;z&uuml;m&uuml;, kişmiş, doşab, şirə və digər qida məhsulları;</span></p>\r\n<p><span>1.0.4.&nbsp;şərab&ccedil;ılıq&nbsp;- &uuml;z&uuml;mdən hazırlanmış şərab və brendi (konyak) istehsalı və saxlanması proseslərini əhatə edən yeyinti sənayesi sahəsi;</span></p>\r\n<p><span>1.0.5.&nbsp;şərab&nbsp;- &uuml;z&uuml;m şirəsindən alınan alkoqollu i&ccedil;ki;</span></p>\r\n<p><span>1.0.6.&nbsp;brendi (konyak)&nbsp;- s&uuml;frə şərabından hazırlanmış spirtin dincə qoyulmaqla yetişdirilməsindən alınan alkoqollu i&ccedil;ki.</span></p>\r\n<p><span>Maddə 2.&nbsp;&Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq haqqında Azərbaycan Respublikasının qanunvericiliyi</span></p>\r\n<p><span>2.1. &Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq haqqında Azərbaycan Respublikasının qanunvericiliyi bu Qanundan, Azərbaycan Respublikasının m&uuml;lki və aqrar qanunvericiliyindən, habelə bu Qanunla əlaqədar qəbul edilmiş digər normativ h&uuml;quqi aktlardan ibarətdir.</span></p>\r\n<p><span>2.2. Vətəndaşların şəxsi istehlakı &uuml;&ccedil;&uuml;n şərab&ccedil;ılıq məhsullarının istehsalı Azərbaycan Respublikasının m&uuml;vafiq qanunvericiliyi ilə tənzimlənir.</span></p>\r\n<p><span>2.3. Azərbaycan Respublikasının tərəfdar &ccedil;ıxdığı d&ouml;vlətlərarası m&uuml;qavilələrdə şərab&ccedil;ılıq məhsullarının istehsalı, d&ouml;vriyyəsi və keyfiyyətinə dair m&uuml;əyyən olunmuş qaydalar bu Qanunda nəzərdə tutulmuş qaydalardan fərqlənərsə, beynəlxalq m&uuml;qavilələrin qaydaları tətbiq edilir.</span></p>\r\n<p><span>Maddə 3.&nbsp;Qanunun məqsədi</span></p>\r\n<p><span>Bu Qanunun məqsədi respublikada &uuml;z&uuml;m&ccedil;&uuml;l&uuml;y&uuml;n və şərab&ccedil;ılığın inkişafını təmin etməklə istehlak bazarına beynəlxalq standartların tələblərinə cavab verən, rəqabət qabiliyyətli &uuml;z&uuml;m və şərab&ccedil;ılıq məhsulları &ccedil;ıxarılmasına təşkilati, iqtisadi və h&uuml;quqi zəmin yaratmaqdır.</span></p>\r\n<p><span>Maddə 4.&nbsp;&Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq sahəsində d&ouml;vlət siyasətinin istiqamətləri&nbsp;</span></p>\r\n<p><span>4.0. &Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq sahəsində d&ouml;vlət siyasətinin istiqamətləri aşağıdakılardır:</span></p>\r\n<p><span>4.0.1. &Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq sahələrinin inkişaf etdirilməsi məqsədilə elmi tədqiqatların m&uuml;asir tələblərə uyğun aparılmasının g&uuml;cləndirilməsi;</span></p>\r\n<p><span>4.0.2. informasiya və təcr&uuml;bə m&uuml;badiləsinin genişləndirilməsi;</span></p>\r\n<p><span>4.0.3. beynəlxalq standartların tələblərinə uyğun məhsul istehsalına təminat verən normativ h&uuml;quqi bazanın yaradılması;</span></p>\r\n<p><span>4.0.4. kadrların hazırlanması və təkmilləşdirilməsi, elmi-təcr&uuml;bi seminarların, konfransların təşkili;</span></p>\r\n<p><span>4.0.5. istehsal olunaraq istehlak bazarına &ccedil;ıxarılan və ya ixrac olunan rəqabət qabiliyyətli &uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının keyfiyyətinin y&uuml;ksəldilməsi &uuml;&ccedil;&uuml;n yeni texnika və m&uuml;asir texnologiyanın tətbiq edilməsi;</span></p>\r\n<p><span>4.0.6. istehsal və ixracın artırılması, daxili bazarın haqsız rəqabətdən qorunması.</span></p>\r\n<p><span>Maddə 5.&nbsp;&Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq sahəsində d&ouml;vlətin vəzifələri</span></p>\r\n<p><span>5.0. &Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq sahəsində d&ouml;vlətin vəzifələri aşağıdakılardır:</span></p>\r\n<p><span>5.0.1. &uuml;z&uuml;m&ccedil;&uuml;l&uuml;y&uuml;n təşkili, &uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının, &uuml;z&uuml;m əkini materiallarının istehsalının, d&ouml;vriyyəsinin və keyfiyyətinin y&uuml;ksəldilməsi tədbirlərinin tənzimlənməsinə dair normativ h&uuml;quqi aktlar qəbul etmək;</span></p>\r\n<p><span>5.0.2. &uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının bazarını &ouml;yrənmək, proqnozlar vermək, sahənin inkişafı &uuml;zrə məqsədli d&ouml;vlət proqramları hazırlamaq və həyata ke&ccedil;irmək;</span></p>\r\n<p><span>5.0.3. Azərbaycan Respublikası ərazisində xəstəlik və zərərvericilərə qarşı d&ouml;z&uuml;ml&uuml; &uuml;z&uuml;m sortlarını yetişdirmək, tinglik təsərr&uuml;fatlarını inkişaf etdirmək, rayonlaşdırılmış &uuml;z&uuml;m sortlarını &ccedil;oxaltmaq, habelə coğrafi adlar verilən keyfiyyətli şərab&ccedil;ılıq məhsullarının istehsalını stimullaşdırmaq məqsədilə investisiya qoymaq, subsidiyalar və uzunm&uuml;ddətli kreditlər vermək;</span></p>\r\n<p><span>5.0.4. şərab&ccedil;ılıq məhsullarının istehsalı və d&ouml;vriyyəsi sahəsində sahibkarlıq fəaliyyətinə&nbsp;x&uuml;susi razılıq (lisenziya)&nbsp;verilməsinin qaydalarını m&uuml;əyyən etmək;</span></p>\r\n<p><span>5.0.5. &uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının idxalını və ixracını tənzimləmək;</span></p>\r\n<p><span>5.0.6. şərab&ccedil;ılıq məhsullarına dair aksiz vergilərinin dərəcələrini m&uuml;əyyən etmək;</span></p>\r\n<p><span>5.0.7. &uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının keyfiyyətinə və təhl&uuml;kəsizliyinə d&ouml;vlət nəzarətini həyata ke&ccedil;irmək;&nbsp;</span></p>\r\n<p><span>&nbsp;5.0.8. qanunvericiliklə m&uuml;əyyən edilmiş digər vəzifələri həyata ke&ccedil;irmək.</span></p>\r\n<p><span>&nbsp;</span></p>\r\n<p><span><strong>II fəsil</strong></span></p>\r\n<p><span><strong>&Uuml;Z&Uuml;M&Ccedil;&Uuml;L&Uuml;Y&Uuml;N TƏŞKİLİ</strong></span></p>\r\n<p><span>Maddə 6.&nbsp;&Uuml;z&uuml;m əkini materiallarının istehsalı</span></p>\r\n<p><span>6.1. Calaqaltı və calaq&uuml;st&uuml; anaclıqların yaradılması &uuml;&ccedil;&uuml;n xəstəliklərə və zərərvericilərə davamlı əkin materialları m&uuml;vafiq elmi tədqiqat m&uuml;əssisələrində və ya m&uuml;qavilə şərtləri ilə elmi tədqiqat m&uuml;əssisələrinin nəzarəti altında ixtisaslaşdırılmış tinglik təsərr&uuml;fatlarında istehsal olunur.</span></p>\r\n<p><span>6.2. &Uuml;z&uuml;ml&uuml;klərin &ccedil;oxaldılmasında istifadə edilən başqa kateqoriyalara aid əkin materialları m&uuml;lkiyyət formasından asılı olmayaraq, &uuml;z&uuml;m&ccedil;&uuml;l&uuml;k fəaliyyəti ilə məşğul olan h&uuml;quqi və fiziki şəxslər tərəfindən istehsal olunur.</span></p>\r\n<p><span>6.3. Azərbaycan Respublikasında istehsal edilən və satılan &uuml;z&uuml;m əkini materiallarına qanunvericiliklə m&uuml;əyyən olunmuş qaydada m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən onların sortluq və əkin keyfiyyətlərini təsdiq edən&nbsp;uyğunluq&nbsp;sertifikatı&nbsp;və fitosanitar vəziyyətinə dair sertifikat verilir.&nbsp;</span></p>\r\n<p><span>Maddə 7.&nbsp;&Uuml;z&uuml;m əkini materiallarının yetişdirilməsi, &uuml;z&uuml;ml&uuml;klərin salınması, əmtəəlik &uuml;z&uuml;m istehsalı texnologiyaları &uuml;zrə normativ sənədlər</span></p>\r\n<p><span>7.1. &Uuml;z&uuml;m əkini materiallarının yetişdirilməsi, &uuml;z&uuml;ml&uuml;klərin salınması, habelə əmtəəlik &uuml;z&uuml;m istehsalı texnologiyaları &uuml;zrə normativ sənədlərə onların istehsal, saxlanma, qablaşdırma, daşınma və satış qaydalarını və şərtlərini, habelə keyfiyyət g&ouml;stəricilərini nizamlayan m&uuml;vafiq icra hakimiyyəti orqanları tərəfindən təsdiq edilmiş standartlar və təlimatlar aiddir.</span></p>\r\n<p><span>7.2. &Uuml;z&uuml;ml&uuml;klərin salınması və &uuml;z&uuml;m əkini materiallarının yetişdirilməsi ilə məşğul olan h&uuml;quqi və fiziki şəxslər onların sort tərkibinin təmizliyinə və fitosanitar vəziyyətinə qanunvericiliklə m&uuml;əyyən olunmuş qaydada məsuliyyət daşıyırlar.</span></p>\r\n<p><span>7.3. Y&uuml;ksək keyfiyyətli (markalı) şərab və brendi (konyak) məhsullarının hazırlanması &uuml;&ccedil;&uuml;n &uuml;z&uuml;ml&uuml;klərin salınması, &uuml;z&uuml;m əkini materiallarının yetişdirilməsi, habelə əmtəəlik &uuml;z&uuml;m istehsalı texnologiyaları &uuml;zrə normativ sənədlərə əməl olunmasına, o c&uuml;mlədən əkin materiallarının sort təmizliyinə, fitosanitar vəziyyətinə, sortluq və əkin keyfiyyətinə m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən nəzarət edilir.</span></p>\r\n<p><span>Maddə 8.&nbsp;İstehsal mənşəyinə g&ouml;rə adlar verilən şərablar hazırlanması &uuml;&ccedil;&uuml;n &uuml;z&uuml;m istehsalının x&uuml;susiyyətləri</span></p>\r\n<p><span>8.1. İstehsal mənşəyinə g&ouml;rə adlar verilən şərablar hazırlanması &uuml;&ccedil;&uuml;n &uuml;z&uuml;m istehsalı m&uuml;vafiq inzibati ərazi vahidində və ya yerdə ərazinin relyefi, torpaq-iqlim və ekoloji şəraitinin x&uuml;susiyyətləri nəzərə alınmaqla m&uuml;əyyən &uuml;z&uuml;m sortlarının x&uuml;susi aqrotexniki &uuml;sullarla yetişdirilməsi əsasında həyata ke&ccedil;irilir.</span></p>\r\n<p><span>8.2. İstehsal mənşəyinə g&ouml;rə adlar verilən şərablar hazırlanması &uuml;&ccedil;&uuml;n &uuml;z&uuml;m sortlarının becərilməsində herbisidlərdən istifadəyə yol verilmir.</span></p>\r\n<p><span>8.3. İstehsal mənşəyinə g&ouml;rə adlar verilən şərablar hazırlanması &uuml;&ccedil;&uuml;n &uuml;z&uuml;m istehsalının texnologiyası q&uuml;vvədə olan normativ sənədlərlə m&uuml;əyyən edilir.</span></p>\r\n<p><span>Maddə 9.&nbsp;&Uuml;z&uuml;m məhsullarının istehsalı</span></p>\r\n<p><span>9.1. &Uuml;z&uuml;m məhsullarının istehsalı m&uuml;vafiq standartların tələblərinə, təsdiq edilmiş aqrotexniki və texnoloji təlimatlara, habelə sanitariya normalarına və qaydalarına uyğun olaraq həyata ke&ccedil;irilir.</span></p>\r\n<p><span>9.2. &Uuml;z&uuml;m məhsullarının tərkibində insan sağlamlığı &uuml;&ccedil;&uuml;n zərərli maddələrin yol verilən hədləri m&uuml;vafiq normativ sənədlərlə m&uuml;əyyən olunur.</span></p>\r\n<p><span>9.3. İstehlak bazarına &ccedil;ıxarılan &uuml;z&uuml;m məhsulları Azərbaycan Respublikasının qanunvericiliyinə uyğun olaraq qablaşdırılır və etiketləşdirilir.</span></p>\r\n<p><span><strong>III fəsil</strong></span></p>\r\n<p><span><strong>ŞƏRAB&Ccedil;ILIQ MƏHSULLARININ İSTEHSALI</strong></span></p>\r\n<p><span>Maddə 10.&nbsp;Şərab&ccedil;ılıq məhsullarının istehsalına &uuml;mumi tələblər</span></p>\r\n<p><span>10.1. Şərab&ccedil;ılıq məhsullarına &uuml;z&uuml;mdən hazırlanmış şərablar, o c&uuml;mlədən şampan şərabları və brendi (konyak) daxildir.</span></p>\r\n<p><span>10.2. Şərab&ccedil;ılıq məhsullarının istehsalı m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən m&uuml;əyyən edilmiş qaydada&nbsp;x&uuml;susi razılıq (lisenziya)əsasında həyata ke&ccedil;irilir.</span></p>\r\n<p><span>10.3. Şərab&ccedil;ılıq məhsulları m&uuml;vafiq standartların tələblərinə, sanitariya normalarına və qaydalarına riayət edilməklə, təsdiq edilmiş texnoloji təlimatlara və resepturaya uyğun istehsal olunur və onların hazırlanmasında yalnız yeyinti məhsullarından alınan etil spirtindən istifadə edilir.</span></p>\r\n<p><span>10.4. Şərab&ccedil;ılıq məhsullarının sinkləşdirilmiş metal, kapron və karton qablara s&uuml;z&uuml;lməsinə və saxlanmasına yol verilmir. Şampan və qazlaşdırılmış şərabların istehsalı və saxlanması proseslərində hermetikləşmə şərtlərinə əməl olunmalıdır.</span></p>\r\n<p><span>Maddə 11.&nbsp;Şərab istehsalı</span></p>\r\n<p><span>11.1. Hazırlanma texnologiyalarına g&ouml;rə şərablar təbii və x&uuml;susi olur.</span></p>\r\n<p><span>11.2. Təbii şərablar &uuml;z&uuml;m&uuml;n tam və ya qismən spirt qıcqırması yolu ilə alınır.</span></p>\r\n<p><span>11.3. X&uuml;susi şərablar &uuml;z&uuml;m və ya şərab materialından m&uuml;xtəlif texnoloji &uuml;sulların tətbiqi ilə alınır. Belə şərablar xammala və hazırlanma texnologiyasına məxsus dad keyfiyyətinə malik olurlar.</span></p>\r\n<p><span>11.4. Keyfiyyət x&uuml;susiyyətlərinə və saxlanma m&uuml;ddətinə g&ouml;rə şərablar adi (ordinar) və y&uuml;ksək keyfiyyətli (markalı) olur.</span></p>\r\n<p><span>11.5. Adi şərablar &uuml;mumi texnologiya &uuml;zrə hazırlanır, məhsul yığımı ilində və ya n&ouml;vbəti ilin əvvəlində satışa buraxılır.</span></p>\r\n<p><span>11.6. Markalı şərablar y&uuml;ksək keyfiyyətli xammaldan, x&uuml;susi texnologiyanın tətbiqi ilə hazırlanır və texnoloji tutumlarda bir ildən &uuml;&ccedil; ilədək dincə qoyulur.</span></p>\r\n<p><span>11.7. Butulkalarda əlavə olaraq x&uuml;susi şəraitdə iki il yarımdan az olmayaraq saxlanılan markalı şərablar kolleksiya şərabları hesab olunur.</span></p>\r\n<p><span>11.8. Azərbaycan Respublikasında istehsal olunan şərabların hazırlanma texnologiyası və keyfiyyəti q&uuml;vvədə olan normativ sənədlərlə ilə tənzimlənir.</span></p>\r\n<p><span>Maddə 12.&nbsp;İstehsal mənşəyinə g&ouml;rə adlar verilən şərabların istehsalı</span></p>\r\n<p><span>12.1. İstehsal mənşəyinə g&ouml;rə şərablara adlar onların hazırlanmasında istifadə edilən &uuml;z&uuml;m sortlarının yetişdirildiyi inzibati ərazi vahidinin və ya yerin adına uyğun olaraq verilir.</span></p>\r\n<p><span>12.2. Bu şərablar m&uuml;əyyən &uuml;z&uuml;m sortlarının y&uuml;ksək keyfiyyətli &uuml;z&uuml;mlərindən ənənəvi və ya orijinal texnologiyaların tətbiqi əsasında hazırlanır və onların istehsalında etil (yeyinti) spirtinin, saxarozanın, &uuml;z&uuml;m konsentratlarının, ətirli və s&uuml;ni boya maddələrinin, digər inqrediyentlərin istifadəsinə yol verilmir.</span></p>\r\n<p><span>12.3. Belə şərabların hazırlanmasında istifadə edilən &uuml;z&uuml;mlərdə şəkərlilik dərəcəsi q&uuml;vvədə olan standartların tələblərinə uyğun olmalıdır. Hər şərab n&ouml;v&uuml;nə onun hazırlanması &uuml;zrə ayrıca texnoloji təlimat tərtib edilir və məhsulun attestasiyası zamanı təsdiq olunur.</span></p>\r\n<p><span>12.4. İstehsal mənşəyinə g&ouml;rə adlar verilən şərabları istehsal edən m&uuml;əssisələrə məhsulun attestasiyasına dair şəhadətnamə təqdim edilir. İstehsal mənşəyinə g&ouml;rə adlar verilən şərabların attestasiyasının m&uuml;ddəti 5 ildir. Bu m&uuml;ddət qurtardıqdan sonra məhsulun attestasiyası yenidən aparılır.</span></p>\r\n<p><span>12.5. İstehsal mənşəyinə g&ouml;rə adlar verilən şərabların istehsalı barədə əsasnamə m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən təsdiq edilir.</span></p>\r\n<p><span>12.6. İstehsal mənşəyinə g&ouml;rə adlar verilmiş şərablar yalnız ş&uuml;şə butulkalara və ya bədii tərtib edilmiş saxsı qablara s&uuml;z&uuml;l&uuml;r, mantar tıxacları ilə kip bağlanır və m&uuml;vafiq qaydada təsdiq edilmiş normativ sənədlərə uyğun olaraq etiketləşdirilir. Etiketlərdə şərabın istehsal olunduğu yer g&ouml;stərilməklə, şərabın adının istehsal mənşəyinə g&ouml;rə verildiyi qeyd olunmalıdır.</span></p>\r\n<p><span>12.7. İstehsal mənşəyinə g&ouml;rə adlar verilmiş şərabların və şərab məhsullarının saxlanma m&uuml;ddəti m&uuml;vafiq standartlarla m&uuml;əyyən edilir.</span></p>\r\n<p><span>Maddə 13.&nbsp;Brendi (konyak) istehsalı</span></p>\r\n<p><span>13.1. Brendi (konyak) istehsalı &uuml;z&uuml;mdən hazırlanmış şərab materialından spirtin &ccedil;əkilməsi, dincə qoyulması və son məhsulun istehsalı kimi texnoloji prosesləri əhatə edir.</span></p>\r\n<p><span>13.2. Keyfiyyət g&ouml;stəricilərinə və konyak spirtinin saxlanma m&uuml;ddətinə g&ouml;rə brendilər (konyaklar) 5 ilə qədər dincə qoyulan adi (ordinar) və 5 ildən artıq dincə qoyulan y&uuml;ksək keyfiyyətli (markalı) brendilərə (konyaklara) b&ouml;l&uuml;n&uuml;r.</span></p>\r\n<p><span>13.3. Y&uuml;ksək keyfiyyətli (markalı) brendi (konyak) istehsalı &uuml;&ccedil;&uuml;n konyak spirti yalnız palıd &ccedil;əlləklərdə və yaxud palıd taxtalar d&ouml;şənmiş emal &ccedil;ənlərində dincə qoyulur.</span></p>\r\n<p><span>13.4. Y&uuml;ksək keyfiyyətli (markalı) brendinin (konyakın) istehsalı proseslərində k&uuml;k&uuml;rd anhidridindən istifadə edilməsinə yol verilmir.</span></p>\r\n<p><span>13.5. Brendi (konyak) m&uuml;vafiq standartların tələblərinə, təsdiq edilmiş texnoloji təlimatlara, habelə sanitariya normalarına və qaydalarına riayət edilməklə istehsal olunur.</span></p>\r\n<p><span>Maddə 14.&nbsp;Şərab&ccedil;ılıq məhsullarına adların verilməsi</span></p>\r\n<p><span>14.1. Şərab&ccedil;ılıq məhsullarının yeni n&ouml;vləri və &ccedil;eşidləri istehsala hazırlanarkən onların n&uuml;munələri tələb olunan sənədlərlə birlikdə m&uuml;vafiq icra hakimiyyəti orqanının mərkəzi dequstasiya komissiyasına təqdim edilməlidir. Şərab&ccedil;ılıq məhsullarının dequstasiyadan ke&ccedil;irilməsi və sənədləşdirilməsi qaydaları m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən təsdiq edilmiş əsasnamə ilə m&uuml;əyyən olunur.</span></p>\r\n<p><span>14.2. Yeni adlara təklif edilən şərab&ccedil;ılıq məhsulları oxşar məhsullardan tərkibinə, fiziki-kimyəvi g&ouml;stəricilərinə, habelə saxlanma şəraitinə və proseslərinə g&ouml;rə m&uuml;tləq fərqlənməli, orqanoleptik g&ouml;stəricilərinə g&ouml;rə isə oxşar olmalıdır. Belə məhsulların hazırlanma texnologiyası məlum məhsul n&ouml;vlərinin istehsal texnologiyalarından fərqlənməyə bilər.</span></p>\r\n<p><span>14.3. Yeni şərab&ccedil;ılıq məhsuluna ad almaq &uuml;&ccedil;&uuml;n istehsal&ccedil;ı m&uuml;vafiq icra hakimiyyəti orqanına ərizə təqdim etməlidir. Ərizə ilə birlikdə aşağıdakı sənədlər və materiallar təqdim olunur:</span></p>\r\n<p><span>14.3.1. məhsula dair annotasiya;</span></p>\r\n<p><span>14.3.2. məhsula ad verilməsi barədə əsaslandırma məktubu;</span></p>\r\n<p><span>14.3.3. məhsulun hazırlanma texnologiyasına dair təlimat;</span></p>\r\n<p><span>14.3.4. məhsulun tərkibi barədə reseptura məlumatları;</span></p>\r\n<p><span>14.3.5. məhsulun oqranoleptik və fiziki-kimyəvi g&ouml;stəriciləri barədə laboratoriya arayışı;</span></p>\r\n<p><span>14.3.6. məhsulun hazırlanmasında istifadə olunan xammal və materialların mənşəyi barədə məlumatlar;</span></p>\r\n<p><span>14.3.7. məhsul buraxılan qabların n&uuml;munələri, habelə xarici tərtibatının, o c&uuml;mlədən etiketlərin eskizləri;</span></p>\r\n<p><span>14.3.8. ekspertiza &uuml;&ccedil;&uuml;n qablaşdırılmış məhsulların n&uuml;munələri.</span></p>\r\n<p><span>14.4. Tərkibi, hazırlanma texnologiyası və saxlanma prosesləri məlum adlarla buraxılan məhsullara uyğun gələn şərab&ccedil;ılıq məhsullarına yeni ad verilməsinə yol verilmir.</span></p>\r\n<p><span>14.5. Eyni ad altında məhsul buraxılmasına dair iki və ya daha &ccedil;ox ərizə daxil olarsa, ilkinlik h&uuml;ququ birinci daxil olan ərizəyə verilir.</span></p>\r\n<p><span>14.6. Qəbul edilmiş ərizə &uuml;zrə m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən m&uuml;vafiq təlimatlara uyğun olaraq məhsulun yeniliyinə g&ouml;rə ekspertizası aparılır.</span></p>\r\n<p><span>14.7. Ekspertiza nəticəsində məhsulun yeniliyi təsdiq olunarsa, m&uuml;vafiq icra hakimiyyəti orqanının qərarı ilə şərab&ccedil;ılıq məhsuluna istehsal&ccedil;ı tərəfindən təklif edilən x&uuml;susi ad verilir.</span></p>\r\n<p><span>14.8. X&uuml;susi ad verilən şərab&ccedil;ılıq məhsulu m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən d&ouml;vlət reyestrində qeydiyyata alınır.</span></p>\r\n<p><span>14.9. Yeni ad altında d&ouml;vlət reyestrində qeydiyyata alınmış şərab&ccedil;ılıq məhsulunun istehsal&ccedil;ılarına məhsulun attestasiyasına dair şəhadətnamə təqdim edilir.</span></p>\r\n<p><span>14.10. Yeni ad altında d&ouml;vlət reyestrində qeydiyyata alınmış, istehsala və istehlak bazarına attestasiya edilmiş şərab&ccedil;ılıq məhsulları Azərbaycan Respublikasının qanunvericiliyinə uyğun olaraq patentləşdirilir.</span></p>\r\n<p><span>14.11. Şərab&ccedil;ılıq məhsullarına adların verilməsi qaydaları m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən təsdiq edilmiş əsasnamə ilə m&uuml;əyyən edilir.</span></p>\r\n<p><span>14.12. Bu Qanun q&uuml;vvəyə mindiyi g&uuml;nədək m&uuml;xtəlif adlar altında d&ouml;vriyyədə olan patentləşməmiş şərab&ccedil;ılıq məhsulları da qanunvericiliyə uyğun olaraq d&ouml;vlət reyestrində qeydiyyata alınmalı, istehlak bazarına attestasiya edilməli və patentləşdirilməlidir.</span></p>\r\n<p><span>14.13. Adlar verilən şərab&ccedil;ılıq məhsullarının ekspertizasına, d&ouml;vlət reyestrində qeydiyyata alınmasına və attestasiyasına g&ouml;rə d&ouml;vlət r&uuml;sumu alınır.&nbsp;</span></p>\r\n<p><span>14.14. Yeni ad altında d&ouml;vlət reyestrində qeydiyyata alınmamış, attestasiyasına dair şəhadətnamə təqdim edilməmiş və patentləşməmiş şərab&ccedil;ılıq məhsullarının sertifikatlaşdırılmasına yol verilmir.</span></p>\r\n<p><span>14.15. Yeni ad altında patent verilmiş şərab&ccedil;ılıq məhsullarının başqa istehsal&ccedil;ılar tərəfindən istehsal edilməsi patent sahibi ilə bağlanmış lisenziya m&uuml;qaviləsi əsasında həyata ke&ccedil;irilə bilər.</span></p>\r\n<p><span>14.16. Lisenziya m&uuml;qaviləsinə g&ouml;rə patent sahibi patent verilmiş şərab&ccedil;ılıq məhsullarının istehsalı &uuml;zrə h&uuml;ququnu, haqqı &ouml;dənilməklə, m&uuml;qavilədə nəzərdə tutulmuş həcmdə başqa istehsal&ccedil;ılara verə bilər. Lisenziya m&uuml;qaviləsi m&uuml;vafiq icra hakimiyyəti orqanında qeydiyyata alınır.</span></p>\r\n<p><span>14.17. Lisenziya m&uuml;qaviləsinin bağlanması qaydaları və şərtləri Azərbaycan Respublikasının qanunvericiliyi ilə tənzimlənir.</span></p>\r\n<p><span>Maddə 15.&nbsp;Şərab&ccedil;ılıq məhsullarının qablaşdırılması, etiketləşdirilməsi, saxlanması və daşınması</span></p>\r\n<p><span>15.1. Şərab&ccedil;ılıq məhsullarının qablaşdırılması, etiketləşdirilməsi, saxlanması və daşınması q&uuml;vvədə olan normativ sənədlərin tələblərinə uyğun olaraq həyata ke&ccedil;irilir.</span></p>\r\n<p><span>15.2. Şərab&ccedil;ılıq məhsulunun hər butulkasının etiketində aşağıdakılar g&ouml;stərilməlidir:</span></p>\r\n<p><span>15.2.1. məhsulun adı və əmtəə-reklam adı;</span></p>\r\n<p><span>15.2.2. istehsal&ccedil;ının adı və əmtəə nişanı;</span></p>\r\n<p><span>15.2.3. məhsulun kondisiyası, kateqoriyası və ya qrupu;</span></p>\r\n<p><span>15.2.4. butulkanın həcmi;</span></p>\r\n<p><span>15.2.5. məhsulun standartı və qablaşdırıldığı tarix;</span></p>\r\n<p><span>15.2.6. sifariş&ccedil;inin təklifi ilə digər məlumatlar.</span></p>\r\n<p><span>15.3. Markalı və kolleksiya şərablarının etiketlərində bu məlumatlardan başqa, &uuml;z&uuml;m&uuml;n yığıldığı il də g&ouml;stərilir.</span></p>\r\n<p><span>15.4. Markalı şərab&ccedil;ılıq məhsullarının arxa etiketlərində onların dincə qoyulma m&uuml;ddəti g&ouml;stərilməlidir.</span></p>\r\n<p><span>15.5. Etiketlərdə g&ouml;stərilən məlumatlar Azərbaycan Respublikasının d&ouml;vlət dilində, məhsulun adı isə həm Azərbaycan Respublikasının d&ouml;vlət dilində, həm də xarici dillərin birində yazılır.</span></p>\r\n<p><span>15.6. İstehsal mənşəyinə g&ouml;rə adlar verilmiş şərabların bədii tərtib edilmiş arxa etiketlərində məhsulun hazırlanma texnologiyası, keyfiyyət g&ouml;stəriciləri, &uuml;z&uuml;m sortunun yayılma arealı barədə də qısa məlumatlar g&ouml;stərilə bilər.</span></p>\r\n<p><span>15.7. İxrac edilən şərab&ccedil;ılıq məhsullarının etiketləri tərəflər arasında bağlanmış m&uuml;qavilə şərtlərinə uyğun tərtib oluna bilər.</span></p>\r\n<p><span>15.8. Şərab&ccedil;ılıq məhsullarının saxlanması və daşınması onların kəmiyyət və keyfiyyət g&ouml;stəricilərinin sabitliyini təmin edən vasitələrlə həyata ke&ccedil;irilir.</span></p>\r\n<p><span>Maddə 16.&nbsp;Şərab&ccedil;ılıq məhsullarının istehsalı &uuml;zrə texnoloji avadanlıqlara dair tələblər</span></p>\r\n<p><span>16.1. Şərab&ccedil;ılıq məhsullarının istehsalı &uuml;zrə texnoloji avadanlıqlar, &ccedil;ənlər və &ouml;l&ccedil;mə cihazları yeyinti sənayesində istifadəsinə icazə verilən materiallardan hazırlanmalı və &ouml;rt&uuml;lməlidir.</span></p>\r\n<p><span>16.2. Bu texnoloji avadanlıqlar m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən m&uuml;əyyən edilmiş qaydada d&ouml;vlət qeydiyyatına alınır.</span></p>\r\n<p><span>16.3. Şərab&ccedil;ılıq məhsullarının istehsalı &uuml;zrə texnoloji avadanlıqlar m&uuml;tləq qaydada spirt&ouml;l&ccedil;mə-nəzarət cihazları və spirt&ouml;l&ccedil;mə sayğacları və avtomatlaşdırılmış (say və həcm &uuml;&ccedil;&uuml;n) u&ccedil;ot sayğacları ilə təchiz edilməlidir. Spirt&ouml;l&ccedil;mə-nəzarət cihazları, spirt&ouml;l&ccedil;mə sayğacları və avtomatlaşdırılmış (say və həcm &uuml;&ccedil;&uuml;n) u&ccedil;ot sayğacları m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən plomblanmalıdır.</span></p>\r\n<p><span><strong>IV fəsil</strong></span></p>\r\n<p><span><strong>&Uuml;Z&Uuml;M&Ccedil;&Uuml;L&Uuml;Y&Uuml;N VƏ ŞƏRAB&Ccedil;ILIĞIN İNKİŞAFININ İQTİSADİ TƏNZİMLƏNMƏSİ</strong></span></p>\r\n<p><span>Maddə 17.&nbsp;&Uuml;z&uuml;m&ccedil;&uuml;l&uuml;y&uuml;n və şərab&ccedil;ılığın maliyyələşdirilməsi</span></p>\r\n<p><span>17.1. Azərbaycan Respublikasında &uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq sahəsində elmi tədqiqat, sort-sınaq və təcr&uuml;bə işlərinin aparılması d&ouml;vlət b&uuml;dcəsindən və digər mənbələr hesabına maliyyələşdirilir.</span></p>\r\n<p><span>17.2. Azərbaycan Respublikasının h&uuml;quqi və fiziki şəxsləri, əcnəbilər, vətəndaşlığı olmayan şəxslər və xarici h&uuml;quqi şəxslər &uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq sahəsində sahibkarlıq fəaliyyətlərini &ouml;z vəsaitləri hesabına və (və ya) cəlb olunan vəsaitlərlə həyata ke&ccedil;irirlər.</span></p>\r\n<p><span>Maddə 18.&nbsp;(&Ccedil;ıxarılıb)&nbsp;</span></p>\r\n<p><span>Maddə 19.&nbsp;&Uuml;z&uuml;m&ccedil;&uuml;l&uuml;y&uuml;n və şərab&ccedil;ılığın inkişafı &uuml;zrə məqsədli kreditlər</span></p>\r\n<p><span>19.1. Kommersiya banklarını &uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq &uuml;zrə d&ouml;vlət proqramları &ccedil;ər&ccedil;ivəsində &uuml;z&uuml;m&ccedil;&uuml;l&uuml;y&uuml;n inkişafı sahəsində həyata ke&ccedil;irilən layihələrin kreditləşdirilməsinə həvəsləndirmək və risklərini azaltmaq məqsədi ilə:</span></p>\r\n<p><span>19.1.1. (&Ccedil;ıxarılıb)&nbsp;</span></p>\r\n<p><span>19.1.2. məqsədli kreditlərə g&ouml;rə bank faizi &uuml;zrə g&uuml;zəştlər d&ouml;vlət b&uuml;dcəsinin fermer təsərr&uuml;fatlarına kredit g&uuml;zəştləri fondu və bu kimi digər vəsaitlər hesabına &ouml;dənilir.</span></p>\r\n<p><span>19.2. &Uuml;z&uuml;m&ccedil;&uuml;l&uuml;y&uuml;n və şərab&ccedil;ılığın inkişafında kooperasiyanı s&uuml;rətləndirmək &uuml;&ccedil;&uuml;n aşağıdakı &uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq subyektlərinə məqsədli kreditlərin ayrılmasında &uuml;st&uuml;nl&uuml;k verilir:</span></p>\r\n<p><span>19.2.1. h&uuml;quqi şəxslərə - 10 hektardan artıq yeni &uuml;z&uuml;m bağlarının salınmasına, 2 hektardan artıq calaqaltı və calaq&uuml;st&uuml; anaclıqların yaradılmasına;</span></p>\r\n<p><span>19.2.2. fiziki şəxslərə - 1 hektardan artıq yeni &uuml;z&uuml;m bağlarının salınmasına, 0,5 hektardan artıq calaqaltı və calaq&uuml;st&uuml; anaclıqların yaradılmasına;</span></p>\r\n<p><span>19.2.3. 50 hektardan az olmayan yeni &uuml;z&uuml;ml&uuml;klərin salınması &uuml;&ccedil;&uuml;n h&uuml;quqi və fiziki şəxslərlə uzunm&uuml;ddətli m&uuml;qavilə bağlamış &uuml;z&uuml;m emalı m&uuml;əssisələrinə.</span></p>\r\n<p><span>19.3. Məqsədli kreditlərin verilməsi, qaytarılması, habelə bank faizi &uuml;zrə g&uuml;zəştlərin &ouml;dənilməsi qaydaları m&uuml;vafiq qanunvericiliklə m&uuml;əyyən olunur.&nbsp;</span></p>\r\n<p><span><strong>V fəsil</strong></span></p>\r\n<p><span><strong>ŞƏRAB&Ccedil;ILIQ MƏHSULLARININ D&Ouml;VRİYYƏSİ</strong></span></p>\r\n<p><span>Maddə 20.&nbsp;Şərab&ccedil;ılıq məhsullarının idxalı və ixracı</span></p>\r\n<p><span>20.1. Şərab&ccedil;ılıq məhsullarının idxalı və ixracı Azərbaycan Respublikasının qanunvericiliyinə uyğun olaraq həyata ke&ccedil;irilir.</span></p>\r\n<p><span>20.2. Şərab&ccedil;ılıq məhsullarının idxalına Azərbaycan Respublikasının qanunvericiliyinə uyğun olaraq tarif kvotası tətbiq edilə bilər.</span></p>\r\n<p><span>20.3. Azərbaycan Respublikasına idxal olunan şərab&ccedil;ılıq məhsullarının keyfiyyəti q&uuml;vvədə olan standartların və gigiyenik normativlərin tələblərinə cavab verməlidir.</span></p>\r\n<p><span>20.4. Fiziki şəxslər tərəfindən &ouml;z istehlakı &uuml;&ccedil;&uuml;n şərab&ccedil;ılıq məhsullarının idxalı Azərbaycan Respublikasının qanunvericiliyinə uyğun olaraq tənzimlənir.&nbsp;</span></p>\r\n<p><span>&nbsp;20.5. Şərab&ccedil;ılıq məhsullarının ixracına məhdudiyyət qoyulmur.</span></p>\r\n<p><span>&nbsp;Maddə 21.&nbsp;Şərab&ccedil;ılıq məhsullarının satışı</span></p>\r\n<p><span>21.1. Şərab&ccedil;ılıq məhsullarının satışı Azərbaycan Respublikasının qanunvericiliyinə və ticarət qaydalarına uyğun olaraq topdan və pərakəndə satış formasında həyata ke&ccedil;irilir.</span></p>\r\n<p><span>21.2. Markalı və kolleksiya şərablarının qablara s&uuml;z&uuml;lmədən a&ccedil;ıq halda pərakəndə satışına yol verilmir.</span></p>\r\n<p><span>21.3. Pərakəndə satışa &ccedil;ıxarılan şərab&ccedil;ılıq məhsullarının m&uuml;şayiətedici sənədlərində Azərbaycan Respublikasının d&ouml;vlət dilində aşağıdakılar barədə məlumatlar olmalıdır:</span></p>\r\n<p><span>21.3.1. məhsulun adı, qiyməti və həcmi, o c&uuml;mlədən istehlak qabında olan həcmi, habelə istehsal edildiyi tarix və istifadəyə yararlılıq m&uuml;ddəti;</span></p>\r\n<p><span>21.3.2. istehsal&ccedil;ının adı, h&uuml;quqi &uuml;nvanı və istehsal olunduğu d&ouml;vlət;</span></p>\r\n<p><span>21.3.3. tələblərinə cavab verməli olduğu d&ouml;vlət standartları və sertifikatlaşdırılması;</span></p>\r\n<p><span>21.3.4. dadına və ətrinə təsir edən əsas inqrediyentlər və əlavələr;</span></p>\r\n<p><span>21.3.5. məhsulun tərkibində insan sağlamlığı &uuml;&ccedil;&uuml;n zərərli maddələrin yol verilən miqdarı.</span></p>\r\n<p><span>21.4. Şərab&ccedil;ılıq məhsullarının pərakəndə satışı aşağıdakı hallarda qadağan olunur:</span></p>\r\n<p><span>21.4.1. saxtalaşdırılması m&uuml;əyyən olunduqda;</span></p>\r\n<p><span>21.4.2. qanunvericiliklə m&uuml;əyyən edilmiş qaydada aksiz markası ilə markalanmadıqda və ya saxta aksiz markaları ilə markalandıqda;</span></p>\r\n<p><span>21.4.3. istehsal markasına uyğun gəlmədikdə;</span></p>\r\n<p><span>21.4.4. məhsulun hər partiyası &uuml;&ccedil;&uuml;n m&uuml;vafiq sertifikatlar olmadıqda;</span></p>\r\n<p><span>21.4.5. keyfiyyəti, qablaşdırılması və etiketləşdirilməsi q&uuml;vvədə olan standartların tələblərinə və gigiyenik normativlərə uyğun gəlmədikdə;</span></p>\r\n<p><span>21.4.6. alıcılar 18 yaşına &ccedil;atmamış şəxslər olduqda;</span></p>\r\n<p><span>21.4.7. məhsulun s&uuml;z&uuml;ld&uuml;y&uuml; qab daxildən və ya xaricdən &ccedil;irkli, &ccedil;&ouml;k&uuml;nt&uuml;l&uuml;, sınıq və ya zədəli olduqda.</span></p>\r\n<p><span>21.5. K&uuml;&ccedil;ələrdə, əllərdə, hərbi, təhsil və tibb m&uuml;əssisələrinin ərazilərində, k&uuml;tləvi tədbirlər və idman yarışları ke&ccedil;irilən yerlərdə, yataqxanalarda, onlara bitişik ərazilərdə və bələdiyyələr və m&uuml;vafiq icra hakimiyyəti orqanları tərəfindən m&uuml;əyyən edilmiş digər yerlərdə şərab&ccedil;ılıq məhsullarının pərakəndə satışına yol verilmir.</span></p>\r\n<p><span>Maddə 22.&nbsp;Şərab&ccedil;ılıq məhsullarının d&ouml;vriyyədən g&ouml;t&uuml;r&uuml;lməsi</span></p>\r\n<p><span>22.1. Şərab&ccedil;ılıq məhsullarının d&ouml;vriyyədən g&ouml;t&uuml;r&uuml;lməsi Azərbaycan Respublikasının qanunvericiliyinə uyğun olaraq m&uuml;vafiq icra hakimiyyəti orqanlarının qərarı ilə həyata ke&ccedil;irilir.</span></p>\r\n<p><span>22.2. Şərab&ccedil;ılıq məhsulları d&ouml;vriyyədən aşağıdakı hallarda g&ouml;t&uuml;r&uuml;l&uuml;r:</span></p>\r\n<p><span>22.2.1. istehsalına və satışına&nbsp;x&uuml;susi razılıq (lisenziya)&nbsp;olmadıqda;<a name="_ednref11"></a><a href="http://e-qanun.az/files/framework/data/1/c_f_1021.htm#_edn11">[11]</a></span></p>\r\n<p><span>22.2.2. məhsulun hər partiyası &uuml;&ccedil;&uuml;n m&uuml;vafiq sertifikatlar olmadıqda;</span></p>\r\n<p><span>22.2.3. q&uuml;vvədə olan normativ sənədlərə uyğun qablaşdırılmadıqda, etiketləşdirilmədikdə, habelə aksiz markası ilə markalanmadıqda və ya saxta markalarla markalandıqda;</span></p>\r\n<p><span>22.2.4. məhsulun tərkibi d&ouml;vlət standartlarının və gigiyenik normativlərin tələblərinə cavab vermədikdə;</span></p>\r\n<p><span>22.2.5. tərkibində olan etil spirtinin ərzaq xammalından istehsal edilmədiyi və ya denaturat əlavələrin olması m&uuml;əyyən edildikdə;</span></p>\r\n<p><span>22.2.6. qanuni istehsalı və d&ouml;vriyyəsi barədə sənədləri olmadıqda;</span></p>\r\n<p><span>22.2.7. sahibsiz olması m&uuml;əyyən edildikdə;</span></p>\r\n<p><span>22.2.8. məhsulun s&uuml;z&uuml;ld&uuml;y&uuml; qab daxildən və ya xaricdən &ccedil;irkli, &ccedil;&ouml;k&uuml;nt&uuml;l&uuml;, sınıq və ya zədəli olduqda.</span></p>\r\n<p><span>22.3. D&ouml;vriyyədən g&ouml;t&uuml;r&uuml;lm&uuml;ş şərab&ccedil;ılıq məhsulları bu Qanunun 22.2.4-c&uuml; və 22.2.5-ci maddələrində g&ouml;stərilən hallarda təkrar emala g&ouml;ndərilir və ya məhv edilir, digər hallarda isə qanunvericiliklə m&uuml;əyyən edilmiş qaydada istifadə edilir.</span></p>\r\n<p><span>22.4. F&ouml;vqəladə vəziyyətin q&uuml;vvədə olduğu m&uuml;ddət ərzində onun tətbiq edildiyi ərazidə şərab&ccedil;ılıq məhsullarının d&ouml;vriyyəsi &uuml;zrə qanunvericiliklə m&uuml;əyyən edilmiş qaydada x&uuml;susi rejim yaradıla bilər.&nbsp;</span></p>\r\n<p><span><strong>VI fəsil</strong></span></p>\r\n<p><span><strong>&Uuml;Z&Uuml;M VƏ ŞƏRAB&Ccedil;ILIQ MƏHSULLARININ KEYFİYYƏTİNİN TƏMİN EDİLMƏSİ</strong></span></p>\r\n<p><span>Maddə 23.&nbsp;&Uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının keyfiyyətinə dair tələblərin normalaşdırılması</span></p>\r\n<p><span>23.1. &Uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının keyfiyyətinin təmin edilməsi sahəsində normalaşdırılma bu məhsulların keyfiyyətinə və təhl&uuml;kəsizliyinə dair tələbləri, onların istehsal, daşınma, saxlanma, qablaşdırma, etiketləşdirmə, markalanma və satış şərtlərini m&uuml;əyyən edən texniki, sanitariya, ekoloji və digər standartlar, norma, qayda və tələblər vasitəsi ilə həyata ke&ccedil;irilir.</span></p>\r\n<p><span>23.2. &Uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının keyfiyyətinə dair tələblərin normalaşdırılması &uuml;zrə normativ sənədlərin icra edilməsi b&uuml;t&uuml;n şərab&ccedil;ılıq fəaliyyəti subyektləri &uuml;&ccedil;&uuml;n məcburidir.</span></p>\r\n<p><span>Maddə 24.&nbsp;&Uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının keyfiyyət g&ouml;stəricilərinin qiymətləndirilməsi</span></p>\r\n<p><span>24.1. Azərbaycan Respublikasında istehsal edilən və respublikaya gətirilən &uuml;z&uuml;m və şərab&ccedil;ılıq məhsulları fiziki-kimyəvi, orqanoleptik, toksikoloji və radioloji g&ouml;stəricilərinə g&ouml;rə təhlil edilir və qiymətləndirilir. Keyfiyyət g&ouml;stəricilərinin qiymətləndirilməsi m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən m&uuml;əyyən edilmiş laboratoriyalarda, vahid metodika &uuml;zrə aparılır.</span></p>\r\n<p><span>24.2. &Uuml;z&uuml;m və şərab&ccedil;ılıq məhsullarının tərkibində insanların həyatı və sağlamlığı &uuml;&ccedil;&uuml;n zərərli maddələrin miqdarı q&uuml;vvədə olan standartlarla və gigiyenik normativlərlə tənzimlənir.</span></p>\r\n<p><span>24.3. Keyfiyyət g&ouml;stəriciləri qiymətləndirilmiş &uuml;z&uuml;m məhsulları, habelə d&ouml;vlət gigiyena qeydiyyatına alınmış şərab&ccedil;ılıq məhsulları m&uuml;vafiq qaydada sertifikatlaşdırılır.</span></p>\r\n<p><span>Maddə 25.&nbsp;Şərab&ccedil;ılıq məhsulları istehsalı m&uuml;əssisələrinin attestasiyası</span></p>\r\n<p><span>25.1. Şərab&ccedil;ılıq məhsulları istehsal edən m&uuml;əssisələr, m&uuml;stəqil sexlər, sınaq laboratoriyaları və digər istehsal b&ouml;lmələri istehsal olunan məhsulların insanların həyatı və sağlamlığına, habelə ətraf m&uuml;hitin m&uuml;hafizəsinə zəmanət verən texniki, ekoloji, sanitariya, yanğından m&uuml;hafizə normalarına və qaydalarına uyğunluğuna g&ouml;rə icbari attestasiyadan ke&ccedil;irilir.</span></p>\r\n<p><span>25.2. Attestasiya m&uuml;əssisələrdə istehsal edilən və istehlak bazarına &ccedil;ıxarılan hər məhsul n&ouml;v&uuml; &uuml;zrə m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən ke&ccedil;irilir və m&uuml;əyyən olunmuş qaydada sənədləşdirilir.</span></p>\r\n<p><span>25.3. Attestasiyanın ke&ccedil;irilməsi qaydaları m&uuml;vafiq icra hakimiyyəti orqanı tərəfindən m&uuml;əyyən olunur.</span></p>\r\n<p><span>Maddə 26.&nbsp;Şərab&ccedil;ılıq məhsullarının saxtalaşdırılmadan qorunması</span></p>\r\n<p><span>26.1. Azərbaycan Respublikasının qanunvericiliyi ilə m&uuml;əyyən olunmuş qaydada&nbsp;x&uuml;susi razılıq (lisenziya)&nbsp;alınmadan, m&uuml;əyyən olunmuş m&uuml;ddətdə attestasiyadan ke&ccedil;irilmədən və təsdiq edilmiş texnoloji normalara uyğun &uuml;sullara, resepturaya və materiallara riayət edilmədən istehsal olunmuş şərab&ccedil;ılıq məhsulları saxta hesab olunur.</span></p>\r\n<p><span>26.2. Saxtalaşdırılmış şərab&ccedil;ılıq məhsullarının istehsalı və d&ouml;vriyyəsi ilə məşğul olan h&uuml;quqi və fiziki şəxslər Azərbaycan Respublikasının qanunvericiliyində nəzərdə tutulmuş qaydada məsuliyyət daşıyırlar.</span></p>\r\n<p><span>26.3. Daxili bazarın saxtalaşdırılmış şərab&ccedil;ılıq məhsullarından qorunması məqsədi ilə şərab&ccedil;ılıq fəaliyyəti subyektləri istehsal etdikləri məhsulları qanunvericiliklə m&uuml;əyyən olunmuş qaydada attestasiyadan ke&ccedil;irməli, sertifikatlaşdırmalı, qablaşdırmalı və məhsulun xarici tərtibatını bu Qanunun 15-ci maddəsi ilə m&uuml;əyyən edilmiş qaydada aparmalıdırlar.</span></p>\r\n<p><span>Maddə 27.&nbsp;Şərab&ccedil;ılıq məhsullarının keyfiyyətinə nəzarət</span></p>\r\n<p><span>27.1. Şərab&ccedil;ılıq məhsullarının keyfiyyətinə nəzarətin məqsədi bu məhsulların istehsalını, d&ouml;vriyyəsini və keyfiyyətini tənzimləyən normativ sənədlərin tələblərinə və gigiyenik normativlərə h&uuml;quqi və fiziki şəxslər tərəfindən əməl olunmasını təmin etməkdir.</span></p>\r\n<p><span>27.2. Şərab&ccedil;ılıq məhsullarının keyfiyyətinə d&ouml;vlət nəzarəti funksiyalarını m&uuml;vafiq icra hakimiyyəti orqanları həyata ke&ccedil;irirlər.</span></p>\r\n<p><span>27.3. Şərab&ccedil;ılıq məhsullarının keyfiyyətinə d&ouml;vlət nəzarətinin həyata ke&ccedil;irilməsi qaydaları m&uuml;vafiq normativ h&uuml;quqi aktlarla m&uuml;əyyən edilir.</span></p>\r\n<p><span><strong><span>27.4. Bələdiyyələr, istehlak&ccedil;ılar və qeyri-h&ouml;kumət təşkilatları şərab&ccedil;ılıq məhsullarının keyfiyyətinə nəzarət funksiyalarını səlahiyyətləri daxilində həyata ke&ccedil;irə bilərlər.</span></strong><strong>&nbsp;</strong></span></p>\r\n<p><span><strong>VII fəsil</strong></span></p>\r\n<p><span><strong>&Uuml;Z&Uuml;M&Ccedil;&Uuml;L&Uuml;K VƏ ŞƏRAB&Ccedil;ILIQ HAQQINDA QANUNVERİCİLİYİN POZULMASINA G&Ouml;RƏ MƏSULİYYƏT VƏ M&Uuml;BAHİSƏLƏRİN HƏLLİ</strong></span></p>\r\n<p><span>Maddə 28.&nbsp;&Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq haqqında qanunvericiliyin pozulmasına g&ouml;rə məsuliyyət</span></p>\r\n<p><span>&Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq haqqında qanunvericiliyin pozulmasında təqsirkar olan h&uuml;quqi və fiziki şəxslər Azərbaycan Respublikasının qanunvericiliyi ilə nəzərdə tutulmuş qaydada məsuliyyət daşıyırlar.</span></p>\r\n<p><span>Maddə 29.&nbsp;M&uuml;bahisələrin həlli</span></p>\r\n<p><span>&Uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və şərab&ccedil;ılıq fəaliyyəti ilə bağlı m&uuml;bahisələr Azərbaycan Respublikasının qanunvericiliyinə uyğun olaraq məhkəmə qaydasında həll edilir.</span></p>\r\n<p><span><strong><br /><strong>Azərbaycan Respublikasının Prezidenti HEYDƏR ƏLİYEV</strong></strong></span></p>\r\n<p><span><strong>Bakı şəhəri, 19 oktyabr 2001-ci il</strong><strong><br /><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;№ 208-IIQ</strong></strong></span></p>', 94, 'az', 0, 1, 1, 0, '3'),
